mapper.select:
select count(a.id) roomCount,count(b.id) rentalRoomCount,
#{userId}
0
userId from room a left join rental_info b on a.id = b.room_id and b.`status` = 1
AND a.user_id = #{userId}

select count(c.id)/count(b.id) * 100 feeCompletion,#{userId} userId,#{month} yearAndMonth from cost_info a left join cost_detail b on a.id = b.cost_info_id LEFT JOIN pay_info c on b.bill_child_number = c.bill_number
left join rental_info d on a.rental_id = d.id left join room e on d.room_id = e.id
where date_format(a.create_time,"%Y-%m") = #{month}
and e.user_id = #{userId}

select sum(a.amount) amount,f.`name` tenantName,concat(g.address,d.room_name) address,datediff(now(),b.create_time) unpairFeeDay from cost_detail a LEFT JOIN cost_info b on a.cost_info_id = b.id left join rental_info c on c.id = b.rental_id left join room d on d.id = c.room_id left join rental_tenant e on c.id = e.rental_id LEFT JOIN tenant_info f on e.tenant_id = f.id left join site g on d.site_id = g.id where a.pay_status = 0 and date_format(b.create_time,"%Y-%m") = #{month}
and d.user_id = #{userId}
GROUP BY c.id order by b.create_time asc

select DATE_FORMAT(c.create_date,#{wildcard}) as day,sum(c.usage_amount) as usageAmount from room a left join device_detail b on a.id = b.room_id right join ${tableName} c on b.id = c.device_id
AND a.user_id = #{logonUserId}
and c.create_date BETWEEN #{beginDate} and #{endDate}
group by day order by c.create_date
